INSERT INTO `areas` (`id`, `componentes`, `areas`, `titulo`, `descripcion`, `url_imagen`) VALUES
(1, 'Comprensión, Interpretación, Evaluación', 'lectura_critica', 'Lectura Crítica', 'Evalúa la capacidad de comprender, interpretar y evaluar textos para tomar decisiones y argumentar una postura crítica.', 'https://media.istockphoto.com/id/1150573135/es/vector/librer%C3%ADa-de-libros-o-libro-de-cartel-de-p%C3%B3ster-del-festival-ilustraci%C3%B3n-vectorial-de.jpg?s=612x612&w=0&k=20&c=Pfsz7PYJduEUaEtrYG4JV77Fjet7up4zHUaQzpjsRqU='),
(2, 'Razonamiento, Geometría, Estadística, Álgebra', 'matematicas', 'Matemáticas', 'Evalúa el razonamiento cuantitativo y la capacidad de solucionar problemas que involucran conceptos matemáticos (aritmética, álgebra, geometría y estadística).', 'https://media.istockphoto.com/id/1492647409/es/vector/asignatura-de-la-escuela-de-matem%C3%A1ticas-alumnos-que-estudian-matem%C3%A1ticas-en-el-aula-maestra.jpg?s=612x612&w=0&k=20&c=-KIBX5buPdPuosd4-SHWYOXZJMgx24HimNhTAhprVX8='),
(3, 'Biología, Química, Física', 'ciencias_naturales', 'Ciencias Naturales', 'Evalúa la comprensión y uso de conceptos de biología, química y física para explicar fenómenos naturales y resolver problemas científicos.', 'https://media.istockphoto.com/id/1307446437/es/vector/el-equipo-de-farmac%C3%A9uticos-lleva-a-cabo-investigaciones-farmacol%C3%B3gicas-personaje-masculino.jpg?s=612x612&w=0&k=20&c=H3wnKj-5BbLr3VD-5cUMh9pb48fPrYXUEtDJh_FCpdw='),
(4, 'Historia, Geografía, Constitución, Democracia', 'ciencias_sociales', 'Sociales y Ciudadanas', 'Evalúa el conocimiento y la aplicación de conceptos de historia, geografía, constitución política y democracia para comprender el entorno social.', 'https://media.istockphoto.com/id/1454778186/es/vector/concepto-de-investigaci%C3%B3n-sociol%C3%B3gica.jpg?s=612x612&w=0&k=20&c=y9sWJC_2ZZnuE0HMSQfhK9Uol8gGHUnXlaRdi9r50EM='),
(5, 'Comprensión, Interpretación', 'ingles', 'Inglés', 'Evalúa la capacidad de comprender textos cortos en inglés, identificando ideas principales y detalles en diferentes contextos.', 'https://media.istockphoto.com/id/1214244642/es/vector/personas-que-aprenden-ilustraci%C3%B3n-vectorial-isom%C3%A9trica-en-ingl%C3%A9s-educaci%C3%B3n-a-distancia.jpg?s=612x612&w=0&k=20&c=YXXpW5ah8S19lQiwIU0pXfmnIsL8cH8R-OwQP4hJZQ8=');

